import React from 'react';
// import StepperPage from '../StepperPage/StepperPage';
import Button from '@material-ui/core/Button';
import { Link } from 'react-router-dom';

function SlideShowPage() {
  const [page, setPage] = React.useState('Welcome to Webiste');
  const step = () => {
    setPage('Thank`s for coming here');
  };
  return (
    <div>
      {page}
      <Button variant="contained">Privious</Button>
      <Button variant="contained" color="primary" onClick={step}>
        <Link to={`/slides/${1}`} >Next</Link>
      </Button>
    </div>
  );
}

export default SlideShowPage;
